// Helper to convert DatabaseMeal to the app's MealCard format
export function toMealCard(dbMeal: DatabaseMeal): {
  id: string;
  title: string;
  description: string;
  prepTimeMin: number;
  level: 'Beginner' | 'Intermediate' | 'Advanced';
  ingredients: { name: string; amount: number; unit: string; category: 'Produce' | 'Protein' | 'Pantry' | 'Dairy' }[];
  shortPrep: string;
} {
  return {
    id: dbMeal.id,
    title: dbMeal.title,
    description: dbMeal.description,
    prepTimeMin: dbMeal.prepTimeMin,
    level: dbMeal.level,
    ingredients: dbMeal.ingredients,
    shortPrep: dbMeal.shortPrep,
  };
}

export function getMealsByType(type: MealTypeTag): DatabaseMeal[] {
  return mealDatabase.filter((m) => m.mealTypes.includes(type));
}

export function searchMealDatabase(query: string, mealType?: MealTypeTag): DatabaseMeal[] {
  const q = query.toLowerCase().trim();
  if (!q) return [];

  const words = q.split(/\s+/);

  const scored = mealDatabase
    .filter((m) => !mealType || m.mealTypes.includes(mealType))
    .map((m) => {
      const text = `${m.title} ${m.description} ${m.dietTags.join(' ')}`.toLowerCase();
      let score = 0;
      for (const word of words) {
        if (m.title.toLowerCase().includes(word)) score += 3;
        if (m.description.toLowerCase().includes(word)) score += 1;
        if (m.dietTags.some((t) => t.includes(word))) score += 1;
      }
      return { meal: m, score };
    })
    .filter((s) => s.score > 0)
    .sort((a, b) => b.score - a.score);

  return scored.slice(0, 20).map((s) => s.meal);
}
